sys_variable_value.value:
(function execute(inputs, outputs) {
    var util = new AmazonECSUtil();
    var session = gs.getSession();
    var query_string = [];
    inputs = util.trimString(inputs);
    var settings = util.buildPayload(inputs.settings, "settings.member.", ["name", "value"]);
    if (settings) query_string.push(settings);
    session.putClientData("SN_AMAZON_ECS_REGION", inputs.region || "us-east-1");
    session.putClientData("SN_AMAZON_ECS_FILTER", query_string.join("&"));
    outputs.region = inputs.region;
    outputs.cluster_name = inputs.cluster_name;
})(inputs, outputs);

