sys_variable_value.value:
(function execute(inputs, outputs) {
    var response = JSON.parse(inputs.response_body);
    if (response && response.Error) {
        outputs.error_message = new AmazonECSErrorHandler().handleError(
            response.Error);
        outputs.status = "Error";
    } else {
        outputs.status = "Success";
        outputs.cluster = response.UpdateClusterSettingsResponse.UpdateClusterSettingsResult.cluster;

    }

})(inputs, outputs);

